__author__ = 'hayssam'
import collections

ref_seq = ""
w_size = 30
with open("nm_sliding_window.txt", "w") as f:
	for w_i in range(len(ref_seq)):
		sub_seq = ref_seq[w_i:w_i + w_size]
		freq = collections.Counter(sub_seq)
		print >> f, w_i+1, ((freq['G'] + freq['C']) * 1.0 / w_size), ref_seq[(w_i+1)-8:(w_i+1)] if w_i>=8 else None, ref_seq[(w_i+1):(w_i+1)+8] if w_i <= len(ref_seq)-8 else None
